mapper.update:
update ${art}
set ${memberNickname} = (
	select ${memberNickname}
	from ${member}
	where ${memberId}=#{memberId}
)
where ${memberId}=#{memberId}

mapper.select:
select b.${artNo}, b.${memberId}, b.${artName}, b.${artPrice}, 
	b.${artContent}, b.${artShowDate}, b.${artReplyCount}, 
	b.${artWishCount}, b.${artViewCount}, b.${artFileName}, b.${memberNickname}
from (
    select rownum rn, a.*
    from(
        select * from ${art} order by ${artNo} desc
    )a
)b
where rn between #{start} and #{end}

select b.${artNo}, b.${memberId}, b.${artName}, b.${artPrice}, 
	b.${artContent}, b.${artShowDate}, b.${artReplyCount}, 
	b.${artWishCount}, b.${artViewCount}, b.${artFileName}, b.${memberNickname}
from (
    select rownum rn, a.*
    from(
        select * from ${art} order by ${artWishCount} desc
    )a
)b
where rn between #{start} and #{end}

select b.${artNo}, b.${memberId}, b.${artName}, b.${artPrice}, 
	b.${artContent}, b.${artShowDate}, b.${artReplyCount}, 
	b.${artWishCount}, b.${artViewCount}, b.${artFileName}, b.${memberNickname}
from (
    select rownum rn, a.*
    from(
        select * from ${art} order by ${artViewCount} desc
    )a
)b
where rn between #{start} and #{end}

select count(*) total_cnt 
from ${art}
where ${artName} like #{keyword}

select count(*) total_cnt 
from ${art}
where ${memberNickname} like #{keyword}

select * 
from ${art}
where ${memberId} = #{memberId}
order by ${artNo} desc

select b.${artNo}, b.${memberId}, b.${artName}, b.${artPrice}, 
	b.${artContent}, b.${artShowDate}, b.${artReplyCount}, 
	b.${artWishCount}, b.${artViewCount}, b.${artFileName}, b.${memberNickname}
from (
    select rownum rn, a.*
    from(
        select * 
        from ${art} 
        where ${artName} like #{keyword}
		order by ${artNo} desc
    )a
)b
where rn between #{start} and #{end}

select b.${artNo}, b.${memberId}, b.${artName}, b.${artPrice}, 
	b.${artContent}, b.${artShowDate}, b.${artReplyCount}, 
	b.${artWishCount}, b.${artViewCount}, b.${artFileName}, b.${memberNickname}
from (
    select rownum rn, a.*
    from(
        select * 
        from ${art} 
        where ${memberNickname} like #{keyword}
		order by ${artNo} desc
    )a
)b
where rn between #{start} and #{end}
